<?phpclass WCV_Admin {	/**	 * The ID of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $plugin_name    The ID of this plugin.	 */	private $plugin_name;	/**	 * The version of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $version    The current version of this plugin.	 */	private $version;	/**	 * Initialize the class and set its properties.	 *	 * @since    1.0.0	 * @param      string    $plugin_name       The name of this plugin.	 * @param      string    $version    The version of this plugin.	 */	public function __construct( $plugin_name, $version ) {        $settingsOptions = get_option( 'max_sb_location_geo_settings' );		$this->plugin_name = $plugin_name;		$this->version = $version;        $this->apiKey = (!empty($settingsOptions['max_sb_location_geo_gmap_api'])) ? $settingsOptions['max_sb_location_geo_gmap_api'] : '';	}	/**	 * Register the stylesheets for the admin area.	 *	 * @since    1.0.0	 */	public function enqueue_styles() {        wp_enqueue_style( 'bootstrap', plugin_dir_url( __FILE__ ) . 'css/admin.css', array(), $this->version, 'all' );	}	/**	 * Register the JavaScript for the admin area.	 *	 * @since    1.0.0	 */	public function enqueue_scripts() {        wp_enqueue_style( 'wp-color-picker' );		wp_enqueue_script( $this->plugin_name,   plugin_dir_url( __FILE__ ) . 'js/admin.js', array( 'jquery', 'wp-color-picker' ), $this->version, true );	}}